var ratio2 = levels && levels[2]
var ratio3 = levels && levels[3]

if( ratio1Upper === 0 ){
    ratio1Upper = options.upper || 62
    ratio1Lower = options.lower || -62

    ratio2Upper = options.upper2 || 45
    ratio2Lower = options.lower2 || -45

    ratio3Upper = options.upper3 || 39
    ratio3Lower = options.lower3 || -39
}

plotline({
    time: time,
    value: ratio3, 
    color: ratio3 < ratio3Lower ? options.warnColor : (ratio3 > ratio3Upper ? options.warnColor : options.ratio3Color)
}, title=S3)

