large-liquidations.script:
// markers needs a serie to get attached to
line($price.close, color=transparent)


if (markers === 0) {
  // first script execution (0 is default values for all persistent variables)

  // Source
  quote = option(default=null,type=list,options=[null, "USD", "USDT", "TUSD", "USDC"],rebuild=true)
  exchange = option(default=null,type=exchange,rebuild=true)
  type = option(default=perp,type=list,options=[null, "spot", "perp"],rebuild=true)
  
  // create a var to store already drawn markers
  markers = []

  // contain the last marker that can change as the bar update
  repaintableMarker = null

  largeLiquidation = options.largeLiquidation || 3000000
}

if (repaintableMarker && repaintableMarker.time < time) {
  // bar is +1 since active marker was added, lock it in the array
  markers.push(repaintableMarker)

  // free up active marker
  repaintableMarker = null
}

var newMarker = null

// BINANCE_FUTURES:btcusdt.lbuy 
// BINANCE_FUTURES:btcusdt.lsell 
lbuyVolume(6) = source(lbuy, quote=quote,type=type,exchange=exchange)
lsellVolume(6) = source(lsell, quote=quote,type=type,exchange=exchange)

// big money strategy
// TODO if it has already been on once, don't show
var signal = sum(lbuyVolume,5) + sum(lsellVolume,5) > largeLiquidation 

if (signal && !iAmOn) {
  
  // newMarker is a temporary variable (not included in the indicator state)
  // to avoid it being treated as persistent variable we wrap it inside parenthesis
  (newMarker) = {
    time: time,
    position: 'inBar',
    color: (options.markerColor || 'blue'),
    shape: 'square'
  }

    iAmOn = true
}else if (!signal){
  iAmOn = false
} 

if (newMarker || (repaintableMarker && !newMarker)) {
  // override persistent variable repaintable marker
  repaintableMarker = newMarker
  
  if (series[0].setMarkers) {
    series[0].setMarkers(markers.concat(repaintableMarker))
  }
}

top-bands-spots.script:
color0 = option(default=rgba(0,0,0,0),type=color)
color1 = option(default=rgba(0,0,0,0),type=color)
color2 = option(default=rgba(0,0,0,0),type=color)


if (!pendingMarkers) {
    // runs only once
    pendingMarkers = [] 

    togglePerp = options.togglePerp || false
    
    moveTo = options.togglePerp ? 10 : -10
    bandSize = options.bandSize || 15

    maxVolume = options.maxVolume || (togglePerp ? 4000 : 1500)
    minVolume = options.minVolume || (togglePerp ? 500 : 200)

    level0 = options.level0 || (togglePerp ? 2000 : 800)
    level1 = options.level1 || (togglePerp ? 1000 : 400)

    startOpacity = options.startOpacity || 0.1

    colorLevel0 = color0.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1,5).map(a => +a)
    colorLevel1 = color1.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1,5).map(a => +a)
    colorLevel = color2.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1,5).map(a => +a)

    colorOpacity0 = colorLevel0.pop()
    colorOpacity1 = colorLevel1.pop()
    colorOpacity2 = colorLevel.pop()

    limitPercent = (options.limitPercent || 100) / 100

    levelsToShow = options.levelsToShow || 5

    printMaxBars = options.printMaxBars || 200
    currentDate = new Date().getTime() / 1000 - 1000 * bar.timeframe 
    // store serie of price level
}

var topLimit = $price.close + $price.close * limitPercent
var bottomLimit = $price.close - $price.close * limitPercent

var levels = togglePerp ? ORDERBOOK:AGGRPERP-BTCUSD.zlevels  : ORDERBOOK:AGGRSPOT-BTCUSD.zlevels

console.log(levels.bids)

if( bar.timestamp < currentDate ){
  return
}
if( !levels ){
  return
}
// avoid too many changes by sorting by price. not volume
levels.bids.sort( (a, b) => a[0] - b[0] ) 
levels.asks.sort( (a, b) => a[0] - b[0] )

var previousPriceMap = priceMap[1]
priceMap(1) = []
//priceMapSerie(1) = {}

for( let i = 0; i < Math.min(levelsToShow, levels.bids.length) ; i++ ){
  if( levels.bids[i][1] > minVolume && (levels.bids[i][0] >= bottomLimit && levels.bids[i][0] <= topLimit) ){

    var alpha = startOpacity + (1 - startOpacity) * ((levels.bids[i][1] - minVolume) / (maxVolume - minVolume))
    var alphaRounded = Math.round(alpha * 100) / 100 
    var colorbase = levels.bids[i][1] > level0 ? colorLevel0 : levels.bids[i][1] > level1 ? colorLevel1 : colorLevel
    var color = 'rgba(' + colorbase.join(',') + ',' + alphaRounded + ')'
    priceMap.push(levels.bids[i][0])

    if( previousPriceMap  && !(previousPriceMap.includes(levels.bids[i][0])) ){
     // console.log('not exist', priceMap[1])
      continue;
    }
    //priceMapSerie[levels.bids[i][0]] = i

   renderer.indicators[indicatorId].series[i] = {
      time: time ,
      lowerValue: levels.bids[i][0],
      higherValue: levels.bids[i][0] + bandSize,
      color: color
    }
  }
}

for( let i = 0; i < Math.min(levelsToShow, levels.asks.length) ; i++ ){
  if( levels.asks[i][1] > minVolume && (levels.asks[i][0] >= bottomLimit && levels.asks[i][0] <= topLimit) ){
    var alpha = startOpacity + (1 - startOpacity) * ((levels.asks[i][1] - minVolume) / (maxVolume - minVolume))
    var alphaRounded = Math.round(alpha * 100) / 100 
    var colorbase = levels.asks[i][1] > level0 ? colorLevel0 : levels.asks[i][1] > level1 ? colorLevel1 : colorLevel
    var color = 'rgba(' + colorbase.join(',') + ',' + alphaRounded + ')'
    priceMap[levels.asks[i][0]] = 10 + i

    if( previousPriceMap  && !(previousPriceMap.includes(levels.asks[i][0])) ){
     // console.log('not exist', priceMap[1])
      continue;
    }
   // priceMapSerie[levels.bids[i][0]] = i

   renderer.indicators[indicatorId].series[10 + i] = {
      time: time ,
      lowerValue: levels.asks[i][0],
      higherValue: levels.asks[i][0] + bandSize,
      color: color
    }
  }
}


return

plotbrokenarea({ time: time, lowerValue: levels.bids[0][0] + moveTo, higherValue: levels.bids[0][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[1][0] + moveTo, higherValue: levels.bids[1][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[2][0] + moveTo, higherValue: levels.bids[2][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[3][0] + moveTo, higherValue: levels.bids[3][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[4][0] + moveTo, higherValue: levels.bids[4][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[5][0] + moveTo, higherValue: levels.bids[5][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[6][0] + moveTo, higherValue: levels.bids[6][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[7][0] + moveTo, higherValue: levels.bids[7][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[8][0] + moveTo, higherValue: levels.bids[8][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.bids[9][0] + moveTo, higherValue: levels.bids[9][0] + bandSize + moveTo } )

plotbrokenarea({ time: time, lowerValue: levels.asks[0][0] + moveTo, higherValue: levels.asks[0][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[1][0] + moveTo, higherValue: levels.asks[1][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[2][0] + moveTo, higherValue: levels.asks[2][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[3][0] + moveTo, higherValue: levels.asks[3][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[4][0] + moveTo, higherValue: levels.asks[4][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[5][0] + moveTo, higherValue: levels.asks[5][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[6][0] + moveTo, higherValue: levels.asks[6][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[7][0] + moveTo, higherValue: levels.asks[7][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[8][0] + moveTo, higherValue: levels.asks[8][0] + bandSize + moveTo } )
plotbrokenarea({ time: time, lowerValue: levels.asks[9][0] + moveTo, higherValue: levels.asks[9][0] + bandSize + moveTo } )

alerts.script:
// markers needs a serie to get attached to
candlestick($price, color=transparent)

if (markers === 0) {
  // first script execution (0 is default values for all persistent variables)

  // create a var to store already drawn markers
  markers = []

  // contain the last marker that can change as the bar update
  repaintableMarker = null
}

if (repaintableMarker && repaintableMarker.time < time) {
  // bar is +1 since active marker was added, lock it in the array
  markers.push(repaintableMarker)

  // free up active marker
  repaintableMarker = null
}

var newMarker = null
var myalert = ORDERBOOK:AGGRSPOT-BTCUSDALERTS.zalert

/*
export const signalStrengthMap: SignalStrength = {
    [Signal.no]: 0,
    [Signal.weak]: 1,
    [Signal.strong]: 2,
    [Signal.vstrong]: 3,
    [Signal.ultimate]: 4,
};
*/
if( myalert && myalert.length ) {
  var bearSignal = myalert[2] == 'sell'
  var bullSignal = myalert[2] == 'buy'

  if (bearSignal) {
    var myColor5 = 'grey'
    if( myalert[3] === 'strong' ){
      myColor5 = 'orange'
    } else if (myalert[3] === 'vstrong' ){
      myColor5 = 'red'
    } else if (myalert[3] === 'ultimate' ){
      myColor5 = 'purple'
    }

    // newMarker is a temporary variable (not included in the indicator state)
    // to avoid it being treated as persistent variable we wrap it inside parenthesis
    (newMarker) = {
      time: time,
      position: 'aboveBar',
      color: myColor5,
      shape: 'arrowDown',
    }
  }

  if (bullSignal) {
    var myColor2 = 'grey'
    if( myalert[3] === 'strong' ){
      myColor2 = 'yellow'
    } else if( myalert[3] === 'vstrong' ){
      myColor2 = 'green'
    } else if( myalert[3] === 'ultimate' ){
      myColor2 = 'purple'
    }

    (newMarker) = {
      time: time,
      position: 'belowBar',
      color:  myColor2,
      shape: 'arrowUp',
    }
  }
}

if (newMarker || (repaintableMarker && !newMarker)) {
  // override persistent variable repaintable marker
  repaintableMarker = newMarker
  
  if (series[0].setMarkers) {
    series[0].setMarkers(markers.concat(repaintableMarker))
  }
}

volume.script:
if (upColor === 0) {
  if (options.showDelta) {
    upColor = options.upBgColor
    downColor = options.downBgColor
  } else {
    upColor = options.upColor
    downColor = options.downColor
  }
}

var _vbuy = source(vbuy, type=perp)
var _vsell = source(vsell, type=perp)

if (options.showDelta) {
  plothistogram({ time: time, value: Math.abs(_vbuy-_vsell), color: _vbuy - _vsell > 0 ? options.upColor : options.downColor})
}

if (options.showVolume) {
  plothistogram({ time: time, value: _vbuy + _vsell, color: _vbuy > _vsell ? upColor : downColor })
}

if (options.showSum) {
  plotline(sum(_vbuy-_vsell, options.sumPeriods || 10))
}

if (options.showAvg) {
  var sumCvd = sum(_vbuy-_vsell, options.sumPeriods || 10)
  plotline(sma(sumCvd, options.avgPeriods || 24))
}

