    var c = m[typeIndex]

    if( showType[c.which]  === false ){
      continue
    }

    for(let i = 0; i < 4; i++){
        if( toAdd[i] === true ){
            _bids += c.bids[i] || 0
            _asks += c.asks[i] || 0
        }
    }
