if (pivotsMax === 0) {
  // Source
  quote = option(default=null,type=list,options=[null, "USD", "USDT", "TUSD", "USDC"],rebuild=true)
  exchange = option(default=null,type=exchange,rebuild=true)
  type = option(default=perp,type=list,options=[null, "spot", "perp"],rebuild=true)
  
  maxBars = options.maxbars || 500

  // Pivots
  pivotsMax = options.ppCheck || 5
  phPositions = []
  plPositions = []
  phVals = []
  plVals = []
  phCvds = []
  plCvds = []
}

buyVolume = source(vbuy, quote=quote,type=type,exchange=exchange)
sellVolume = source(vsell, quote=quote,type=type,exchange=exchange)

cvd(500) = cum(buyVolume - sellVolume)

plotline(cvd, title=options.title || "CVD", color=options.cvdColor)

if( options.toggleShowPricePivot ){
  plotline(cvd, color=transparent)
}

if (options.showDivs) {
  
  // <markers utils>
  if (markersDiv === 0) {
    markersDiv = []
  }
  if (markersPivot === 0) {
    markersPivot = []
  }
  
  if (pendingMarkerDiv && pendingMarkerDiv.time < time) {
    markersDiv.push(pendingMarkerDiv)
    pendingMarkerDiv = null
  }

  if (pendingMarkerPivot && pendingMarkerPivot.time < time) {
    markersPivot.push(pendingMarkerPivot)
    pendingMarkerPivot = null
  }
  
  var newMarkerDiv = null
  var newMarkerPivot = null
  // </markers utils>
  
  // Check if we get new Pivot High Or Pivot Low

  // find pivots low (cvd and price)
  //plCvd(10) = cvd //pivot_low(cvd, options.pivotLeftLength, options.pivotRightLength)
  var plPrice = pivot_low($price.low, options.pivotLeftLength, options.pivotRightLength)

  // find pivots high (cvd and price)
  //phCvd(10) = cvd // pivot_high(cvd, options.pivotLeftLength, options.pivotRightLength)
  var phPrice = pivot_high($price.high, options.pivotLeftLength, options.pivotRightLength)
  
  // if we have a new pivot point
  if( plPrice ){
    // get its CVD
    var plCvd = cvd[options.pivotRightLength-1]
    // add new pivot at beginning of array
    plPositions.unshift(bar.length)
    // add price pivot at beginning of array
    plVals.unshift(plPrice)
    plCvds.unshift(plCvd)
  
    // max pivots in memory. remove last 
    if( plPositions.length > pivotsMax ){
      plPositions.pop();
      plVals.pop()
      plCvds.pop()
    }

    var foundDiv = false
    var startPoint = 0
   // cvd[100] // looks like required to load 
    // Go through each previous pivot
    for( var i = 0; i < (options.ppCheck || 10); i++ ){ 
      if( i > plPositions.length || previousBarLength == 0){
        break
      }
      var previousPrice = plVals[i]
      var previousBarLength = plPositions[i]
      var previousCvd = plCvds[i]

      // Find distance to the previous pivot. where pivot is (bar - pivot) 
      var len = bar.length - options.pivotRightLength - 1 - previousBarLength
      if( len > maxBars){
        break
      }
      
      var hiddenDiv = plPrice > previousPrice && phCvd < previousCvd
      var regularDiv = plPrice < (previousPrice || 0) && plCvd > previousCvd

      // Price lower than previous pivot but CVD is higher
      if( /*len > 5 &&*/ hiddenDiv || hiddenDiv ){
         console.log('PL', plPrice, plCvd, previousPrice, previousCvd) 
         foundDiv = true

         newMarkerDiv = { 
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.DivcolorUp,
          position: 'belowBar',
          //shape: 'arrowDown',
          text: '' + (options.togglePivotAgo ? i + 1 : '') + (hiddenDiv ? '.' : '')
        }
        break
      }
    }

    if( !foundDiv && options.toggleShowPricePivot){
      newMarkerPivot = {
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.DivcolorUp,
          position: 'belowBar',
          //shape: 'arrowUp',
          text:  'L'
        }
    }
  }

  if( phPrice ){
    var phCvd = cvd[options.pivotRightLength-1]
    phPositions.unshift(bar.length)
    phVals.unshift(phPrice)
    phCvds.unshift(phCvd)
    if( phPositions.length > pivotsMax ){
      phPositions.pop();
      phVals.pop()
      phCvds.pop()
    }

    var foundDiv = false
    var startPoint = 0
   // cvd[100] // looks like required to load 
    for( var i = 0; i < (options.ppCheck || 10); i++ ){ 
      if( i > phPositions.length || phPositions[i] == 0){
        break
      }
      var previousPrice = phVals[i]
      var previousBarLength = phPositions[i]
      var previousCvd = phCvds[i]

      // previousPivotDistance
      var len = bar.length - options.pivotRightLength - 1 - previousBarLength

      /*if( len > (options.maxbars || 100)){
        break
      }*/
      var hiddenDiv = phPrice < previousPrice && phCvd > previousCvd
      var regularDiv = phPrice > (previousPrice || Infinity) && phCvd < previousCvd

      // Price greater than previous pivot but CVD lower
      if( /*len > 5 &&*/ regularDiv || hiddenDiv ){
         console.log('PH', phPrice, phCvd, previousPrice, previousCvd)
        foundDiv = true
         newMarkerDiv = { 
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.DivcolorDn,
          position: 'aboveBar',
          //shape: 'arrowDown',
          text: '' + (options.togglePivotAgo ? i + 1 : '') + (hiddenDiv ? '.' : '')
        }
        break
      }
    }

    if( !foundDiv  && options.toggleShowPricePivot ){
        newMarkerPivot = { 
            time: time - (options.pivotRightLength-1) * bar.timeframe,
            color: options.DivcolorDn,
            position: 'aboveBar',
            //shape: 'arrowDown',
            text: 'H'
          }
    }
  }
  
  // <markers utils>
  if (newMarkerDiv || (pendingMarkerDiv && !newMarkerDiv)) {
      pendingMarkerDiv = newMarkerDiv
    
    if (markersDiv.length > 100) {
        markersDiv.splice(0, 1)
    }
      
    if (series[0].setMarkers) {
      series[0].setMarkers(markersDiv.concat(pendingMarkerDiv))
    }
  }

  if (newMarkerPivot || (pendingMarkerPivot && !newMarkerPivot)) {
      pendingMarkerPivot = newMarkerPivot
    
    if (markersPivot.length > 100) {
        markersPivot.splice(0, 1)
    }
      
    if (series[1].setMarkers) {
      series[1].setMarkers(markersPivot.concat(pendingMarkerPivot))
    }
  }

  // </markers utils>
}

