if ( Math.abs(_bids - _asks) > highDiff) {
    brokenarea({
      time: time, 
      lowerValue: _asks,
      higherValue: _bids,
      extendRight: false,
      color: (_bids - _asks ? options.positiveColor : options.negativeColor)
    })
}

