volume-strength.script:
candlestick($price, color=transparent)

if (markers === 0) {
  // first script execution (0 is default values for all persistent variables)

  // create a var to store already drawn markers
  markers = []

  // contain the last marker that can change as the bar update
  repaintableMarker = null

  binanceVlen = options.binanceVlen || 2.9
  binancePerpVlen  = options.binancePerpVlen || 5.8
  coinbaseVlen   = options.coinbaseVlen || 5
  bybitVlen   = options.bybitVlen || 5.9
}

if (repaintableMarker && repaintableMarker.time < time) {
  // bar is +1 since active marker was added, lock it in the array
  markers.push(repaintableMarker)

  // free up active marker
  repaintableMarker = null
}

var newMarker = null

var binanceVolume = BINANCE:btcusdt.vbuy + BINANCE:btcusdt.vbuy
var binancePerpVolume = BINANCE_FUTURES:btcusdt.vbuy + BINANCE_FUTURES:btcusdt.vsell
var coinbaseVolume = COINBASE:BTC-USD.vbuy + COINBASE:BTC-USD.vsell
var bybitVolume = BYBIT:BTCUSDT.vbuy + BYBIT:BTCUSDT.vsell

var vnBinance = binanceVolume / ema(binanceVolume, 20)
var vnBinancePerp = binancePerpVolume / ema(binancePerpVolume, 20)
var vnCoinbase = coinbaseVolume / ema(coinbaseVolume, 20)
var vnBybit = bybitVolume / ema(bybitVolume, 20)

var draw = false
var myLabel = '';

if (vnBinance > binanceVlen ) {
  myLabel += 'Bin '
  draw = true
}
if (vnBinancePerp > binancePerpVlen ) {
  myLabel += 'P '
  draw = true
}
if (vnCoinbase > coinbaseVlen ) {
  myLabel += 'C '
  draw = true
}
if (vnBybit > bybitVlen ) {
  myLabel += 'BB '
  draw = true
}

var isSell  = $price.close > $price.open

if( draw && isSell ){
   (newMarker) = {
    time: time,
    position: 'aboveBar',
    color:  'orange',
    shape: 'arrowDown',
    text:  myLabel
  }
}else if ( draw ){
  (newMarker) = {
    time: time,
    position: 'belowBar',
    color:  'yellow',
    shape: 'arrowUp',
    text:  myLabel
  }
}

if (newMarker || (repaintableMarker && !newMarker)) {
  // override persistent variable repaintable marker
  repaintableMarker = newMarker
  
  if (series[0].setMarkers) {
    series[0].setMarkers(markers.concat(repaintableMarker))
  }
}

