priceRef(92) = $price.close

var localDateTime = new Date(renderer.timestamp*1000);

// oOnly within 2 weeks
if( new Date().getTime() - localDateTime.getTime() > 1209600000 ){
  //return
}

// NZ Tuesday 15.45
if( localDateTime.getDay() == 2 && localDateTime.getHours() == 15 && localDateTime.getMinutes() === 45  ){
  
  // 23 hours
