    bitmexColor = options.bitmexColor.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1, 5).map(a => +a);

    //allOnColor = options.allOnColor.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1,5).map(a => +a)

    spotOpacity = spotColor.pop()
