   // allOnColorOpacity = allOnColor.pop()

    startOpacity = options.startOpacity || 0.1
  
    spotBuyAlerts  = [40, 63, 46, 60]
    spotSellAlerts = [-40, -63, -46, -60]

    perpBuyAlerts  = [38, 27, 36, 35]
    perpSellAlerts = [-20, -27, -36, -35]

    binanceBuyAlerts  = [70, 70, 70, 70]
    binanceSellAlerts = [-70, -70, -70, -70]

    coinbaseBuyAlerts  = [50, 50, 50, 50]
    coinbaseSellAlerts = [-50, -70, -65, -50]

    bitmexBuyAlerts  = [-50, -60, -50, -50]
    bitmexSellAlerts = [-50, -60, -50, -50]

    alerts = {
      spot: {
        buy: spotBuyAlerts,
        sell: spotSellAlerts
      },
      perp: {
        buy: perpBuyAlerts,
        sell: perpSellAlerts
      },
      binance: {
        buy: binanceBuyAlerts,
        sell: binanceSellAlerts
      },
      coinbase: {
        buy: coinbaseBuyAlerts,
        sell: coinbaseSellAlerts
      },
        bitmex: {
            buy: bitmexBuyAlerts,
            sell: bitmexSellAlerts
        }
    }

    whichColors = {
      spot: spotColor,
      perp: perpColor,
      binance: binanceColor,
      coinbase: coinbaseColor,
      bitmex: bitmexColor
    }

    allOnIndicator = options.toggleAllOnIndicator || false,

    showType = {
      spot: options.toggleSpot,
      perp: options.togglePerp,
      binance: options.toggleBinance,
      coinbase: options.toggleCoinbase,
      bitmex: options.toggleBitmex
    }

    showBand = [
        options.toggleBand1 || false,
        options.toggleBand2 || false,
        options.toggleBand3 || false,
        options.toggleBand4 || false,
    ]

    lowerPercent = [options.band0lowerPercent || 0.11, 1, 2.5, 5]
    upperPercent = [1, 2.5, 5, 10]  

    timeframeMs = (renderer.timeframe * 1000)

  }
  // </STARTUP SCRIPT>

  // TODO not working need to loo at previous 
var isLastBar = false // renderer.timestamp *1000 === Math.floor(new Date().getTime() / timeframeMs) * timeframeMs
var ratiosPerp = ORDERBOOK:AGGRPERP-BTCUSD.zratios
var ratiosSpot = ORDERBOOK:AGGRSPOT-BTCUSD.zratios 
var ratiosBinance = renderer.sources['ORDERBOOK:BINANCE-BTCUSDTLEVELS'] && renderer.sources['ORDERBOOK:BINANCE-BTCUSDTLEVELS'].zratios
var ratiosCoinbase = renderer.sources['ORDERBOOK:COINBASE-BTCUSD'] && renderer.sources['ORDERBOOK:COINBASE-BTCUSD'].zratios 
var ratiosBitmex = renderer.sources['ORDERBOOK:BITMEX-XBTUSDLEVELS'] && renderer.sources['ORDERBOOK:BITMEX-XBTUSDLEVELS'].zratios

var m = [
    { which: 'perp', ratios: ratiosPerp || [], bands: [] },
    { which: 'spot', ratios: ratiosSpot || [], bands: [] },
    { which: 'binance', ratios: ratiosBinance || [], bands: [] },
    { which: 'coinbase', ratios: ratiosCoinbase || [], bands: [] },
    { which: 'bitmex', ratios: ratiosBitmex || [], bands: [] }
];
  
  /*var ratiosPerp = ORDERBOOK:AGGRPERP-BTCUSD.zratios
  var ratiosSpot = ORDERBOOK:AGGRSPOT-BTCUSD.zratios 
  var ratiosBinance = renderer.sources['ORDERBOOK:BINANCE-BTCUSDTLEVELS'].zratios
  var ratiosCoinbase = renderer.sources['ORDERBOOK:COINBASE-BTCUSD'].zratios 

  var perps = []
  var spots = []
  var binance = []
  var coinbase = []
*/
  //var ratio = 0

// for each ratio
for(let ratioIndex = 0; ratioIndex < 4; ratioIndex++){

  // TODO must be same direction
  var allOn = true;

  // for each type (perp, spot, binance, ...)
  for(let typeIndex = 0; typeIndex < m.length; typeIndex++){

    var c = m[typeIndex]

    if( showBand[ratioIndex] === false || showType[c.which]  === false ){
      c.bands.push([0, 0, 'rgba(255,255,255,0)']) 
      continue
    }
      
    /*
    var ratioPerp = ratiosPerp && ratiosPerp.length > ratioIndex && ratiosPerp[ratioIndex]
    var ratioSpot =  ratiosSpot && ratiosSpot.length > ratioIndex && ratiosSpot[ratioIndex]
    var ratioBinance =  ratiosBinance && ratiosBinance.length > ratioIndex && ratiosBinance[ratioIndex]
    var ratioCoinbase =  ratiosCoinbase && ratiosCoinbase.length > ratioIndex && ratiosCoinbase[ratioIndex]
    */
    var ratio = c.ratios.length && c.ratios[ratioIndex]
    var direction = Math.sign(ratio)
    var buyAlert = alerts[c.which]['buy'][ratioIndex]
    var sellAlert = alerts[c.which]['sell'][ratioIndex]
    var threshold = direction > 0 ? buyAlert : sellAlert
    var thresholdAbs = Math.abs(threshold)
  
    var lower = $price.close + -1 * direction * (($price.close  * lowerPercent[ratioIndex]) / 100)  
    var upper = $price.close + -1 * direction * (($price.close  * upperPercent[ratioIndex]) / 100)

    if( !ratio || Math.abs(ratio) < thresholdAbs ){
        allOn = false
        c.bands.push([0, 0, 'rgba(255,255,255,0)']) 
    }else{

        var alphaRatio = Math.abs(ratio) < thresholdAbs ? 0 : startOpacity + (1 - startOpacity) * ((Math.abs(ratio) - thresholdAbs) / (100 - thresholdAbs))
        var alphaRatioRounded = Math.round(alphaRatio * 100) / 100 
        var colorRatio = 'rgba(' + whichColors[c.which].join(',') + ',' + alphaRatioRounded + ')'
        
        c.bands.push([lower, upper, colorRatio])
    }
    
  }// end for each type

  if( allOnIndicator && allOn ){
    for(let typeIndex = 0; typeIndex < 4; typeIndex++){
      var c = m[typeIndex]
      c.bands[ratioIndex] = [lower, upper, options.allOnColor]
    }
  }

} // end for each ratios


var i = 0

for(let ratioIndex = 0; ratioIndex < 4; ratioIndex++){

    if( showBand[ratioIndex] === false ){
      i = i + m.length
      continue
    }

    for(let typeIndex = 0; typeIndex < m.length; typeIndex++){

       var c = m[typeIndex]
    
       // Print band if not 0 or i flast bar (so it removes previous)
       if( c.bands.length && ( isLastBar || c.bands[ratioIndex][0] !== 0) ) {
         renderer.indicators[indicatorId].series[i] = {
          time: time, 
          lowerValue: c.bands[ratioIndex][0],
          higherValue: c.bands[ratioIndex][1],
          color:  c.bands[ratioIndex][2]
        }
      }

      i++
    }
}



return
