// <STARTUP SCRIPT> 
if (!boundaries) {
  nyStart = option(default=8,type=range,min=0,max=12,step=1)
  nyEnd = option(default=11,type=range,min=0,max=12,step=1)

  boundaries = {}
  if (series[0].setExtensionsBoundaries) {
    series[0].setExtensionsBoundaries(boundaries)
  }

  // Get the time offset for New York timezone (Eastern Time)
  utcOffset = -5 * 60; // New York is UTC-5
  const date = new Date();
  const jan = new Date(date.getFullYear(), 0, 1);
  const jul = new Date(date.getFullYear(), 6, 1);
  const stdTimezoneOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
  isDST = date.getTimezoneOffset() < stdTimezoneOffset;
}
// </STARTUP SCRIPT> 

var nyDateTime = new Date(renderer.timestamp*1000);

var dayOfWeek = nyDateTime.getUTCDay();

// Check if it's Saturday (6) or Sunday (7) 
var isWeekEnd =  dayOfWeek === 6 || dayOfWeek === 0;

if( isWeekEnd ){
  return
}

const offset = utcOffset + (isDST ? 60 : 0); // Adjust for daylight saving time
const nyHour = nyDateTime.getUTCHours() + offset / 60;

var isOpening = nyHour >= nyStart && nyHour < nyEnd;

if( isOpening ){

  renderer.indicators[indicatorId].series[0] = {
    time: time,
    lowerValue: 0,
    higherValue: 100000,
    extendRight: false
  }

}

return 
  
