obv-spots.script:
if (markers === 0) {
  // first script execution (0 is default values for all persistent variables)
  // Source
  quote = option(default=null,type=list,options=[null, "USD", "USDT", "TUSD", "USDC"],rebuild=true)
  exchange = option(default=null,type=exchange,rebuild=true)
  type = option(default=perp,type=list,options=[null, "spot", "perp"],rebuild=true)
  
  // create a var to store already drawn markers
  markers = []

  // contain the last marker that can change as the bar update
  repaintableMarker = null

  // if candle body has a price percent change less than bodyPercentWatch, we start a refinement
  bodyPercentWatch = options.bodyPercentWatch || 0.3

  // if a wick is x times greater than the other, we might use it to refine change the side
  wickRatioThreshold = options.wickRatioThreshold || 1.5

  // paint a marker when OBV has been refined (changed of direction)
  toggleDebug = options.toggleDebug || false

  // when on, we calculate the wick percent difference and keep only that volume to add to OBV 
  toggleVolumeWick = options.toggleVolumeWick || false

  togglePrintNormalObv = options.togglePrintNormalObv || false


  // Pivots
  pivotsMax = 20
  phPositions = []
  plPositions = []
  phVals = []
  plVals = []
  plInd  = []
  phInd  = []
}
if ( options.toggleDebug && repaintableMarker && repaintableMarker.time < time) {
  // bar is +1 since active marker was added, lock it in the array
  markers.push(repaintableMarker)

  // free up active marker
  repaintableMarker = null
}

var newMarker = null

var _vbuy = source(vbuy, quote=quote,type=type,exchange=exchange)
var _vsell = source(vsell, quote=quote,type=type,exchange=exchange)
var _open = $price.open
var _high = $price.high
var _low = $price.low
var _close = $price.close

var sign = _close > _open ? 1 : -1
var volume = _vbuy + _vsell

var bodySize = Math.abs(_close - _open);
var priceChange = (_close - _open) / _open; // Percentage change

if( togglePrintNormalObv ){
    plotline(cum(sign * volume), title="Original", color=options.originalObvColor)
}

var improved = false

// Check if price change is less than 0.4%
if (options.toggleRefine && Math.abs(priceChange) < bodyPercentWatch/100 ) {
    var upperWickSize = _high - _close;
    var lowerWickSize = _close - _low;

    // Calculate the thresholds
    var upperWickThreshold = wickRatioThreshold * lowerWickSize;
    var lowerWickThreshold = wickRatioThreshold * upperWickSize;

    // Check if upper and lower wicks have similar sizes
    if (upperWickSize < upperWickThreshold && lowerWickSize < lowerWickThreshold) {
        // Neutral move, don't add volume
        sign = 0
        improved = true
    }else{
        // Calculate the thresholds
        var upperWickThreshold = wickRatioThreshold * lowerWickSize;
        var lowerWickThreshold = wickRatioThreshold * upperWickSize;

        // Determine the side based on wick sizes
        // upper: 500, lower 100
        if (upperWickSize > upperWickThreshold) {
            improved = sign !== -1
            // More selling happened, add volume as negative
            sign = -1
            if( toggleVolumeWick ) {
                improved = true
                var percentageDifference = ((upperWickSize - lowerWickSize) / upperWickSize);
                volume = percentageDifference * volume
                
            }
        } else if (lowerWickSize > lowerWickThreshold) {
            improved = sign !== 1
            // More buying happened, add volume as positive
            sign = 1
            if( toggleVolumeWick ) {
                improved = true
                var percentageDifference = ((lowerWickSize - upperWickSize) / lowerWickSize);
                volume = percentageDifference * volume
            }
        }
    }

    if( improved && toggleDebug){
        if (sign == -1) {
            // newMarker is a temporary variable (not included in the indicator state)
            // to avoid it being treated as persistent variable we wrap it inside parenthesis
            (newMarker) = {
                time: time,
                position: 'aboveBar',
                color: 'red',
                shape: 'arrowDown'
            }
        }

        if (sign == 1) {
            (newMarker) = {
                time: time,
                position: 'belowBar',
                color: 'lime',
                shape: 'arrowUp'
            }
        } 
    }
}


if ( options.toggleDebug && (newMarker || (repaintableMarker && !newMarker))) {
  // override persistent variable repaintable marker
  repaintableMarker = newMarker
  
  if (series[0].setMarkers) {
    series[0].setMarkers(markers.concat(repaintableMarker))
    series[1].setMarkers(markers.concat(repaintableMarker))
  }
}

obv(200) = 0 + cum(sign * volume)
plotline(obv, title=options.title, color=options.refinedObvColor)
if( options.toggleShowPricePivot ){
  plotline(obv, color=transparent)
}


if (options.showDivs) {
  
  // <markers utils>
  if (markersDiv === 0) {
    markersDiv = []
  }
  if (markersPivot === 0) {
    markersPivot = []
  }
  
  if (pendingMarkerDiv && pendingMarkerDiv.time < time) {
    markersDiv.push(pendingMarkerDiv)
    pendingMarkerDiv = null
  }

  if (pendingMarkerPivot && pendingMarkerPivot.time < time) {
    markersPivot.push(pendingMarkerPivot)
    pendingMarkerPivot = null
  }
  
  var newMarkerDiv = null
  var newMarkerPivot = null
  // </markers utils>
  
  // Check if we get new Pivot High Or Pivot Low

  // find pivots low (cvd and price)
  //plCvd = pivot_low(obv, options.pivotLeftLength, options.pivotRightLength)
  var plPrice = pivot_low($price.low, options.pivotLeftLength, options.pivotRightLength)

  // find pivots high (cvd and price)
  //phCvd = pivot_high(obv, options.pivotLeftLength, options.pivotRightLength)
  var phPrice = pivot_high($price.high, options.pivotLeftLength, options.pivotRightLength)
  
  if( plPrice ){
    var plObv = obv[options.pivotRightLength - 1]
    plPositions.unshift(bar.length)
    plVals.unshift(plPrice)
    plInd.unshift(plObv)
   
    if( plPositions.length > pivotsMax ){
      plPositions.pop();
      plVals.pop()
      plInd.pop()
    }

    var foundDiv = false
  
    for( var i = 0; i < (options.ppCheck || 10); i++ ){ 
      if( i > plPositions.length || plPositions[i] == 0){
        break
      }
      
      var regular = plPrice < plVals[i] && plObv > plInd[i]
      var hidden = plPrice > plVals[i] && plObv < plInd[i]

      // Price greater than previous pivot but OBV lower
      if( regular || hidden ){
         foundDiv = true

         newMarkerDiv = { 
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.refinedObvColor,
          position: 'belowBar',
          //shape: 'arrowDown',
          text: '' + (options.togglePivotAgo ? i + 1 : '') + (hidden ? '.' : '')
        }
        break
      }
    }

    if( !foundDiv && options.toggleShowPricePivot){
      newMarkerPivot = {
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.refinedObvColor,
          position: 'belowBar',
          //shape: 'arrowUp',
          text:  'L'
        }
    }
  }

  if( phPrice ){
    var phObv = obv[options.pivotRightLength - 1]
    phPositions.unshift(bar.length)
    phVals.unshift(phPrice)
    phInd.unshift(phObv)

    if( phPositions.length > pivotsMax ){
      phPositions.pop();
      phVals.pop()
      phInd.pop()
    }

    var foundDiv = false

    for( var i = 0; i < (options.ppCheck || 10); i++ ){ 
      if( i > phPositions.length || phPositions[i] == 0){
        break
      }

      var regular = phPrice > (phVals[i] || Infinity) && phObv < phInd[i]
      var hidden = phPrice < phVals[i] && phObv > phInd[i] 

      // Price greater than previous pivot but OBV lower
      if( regular || hidden ){
        foundDiv = true
         newMarkerDiv = { 
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.refinedObvColor,
          position: 'aboveBar',
          //shape: 'arrowDown',
          text: '' + (options.togglePivotAgo ? i + 1 : '')  + (hidden ? '.' : '')
        }
        break
      }
    }

   // console.log(time, $price.high);
    if( !foundDiv  && options.toggleShowPricePivot ){
        newMarkerPivot = { 
            time: time - (options.pivotRightLength-1) * bar.timeframe,
            color: options.refinedObvColor,
            position: 'aboveBar',
            //shape: 'arrowDown',
            text: 'H'
          }
    }
  }
  
  // <markers utils>
  if (newMarkerDiv || (pendingMarkerDiv && !newMarkerDiv)) {
      pendingMarkerDiv = newMarkerDiv
    
    if (markersDiv.length > 100) {
        markersDiv.splice(0, 1)
    }
      
    if (series[0].setMarkers) {
      series[0].setMarkers(markersDiv.concat(pendingMarkerDiv))
    }
    if (series[1].setMarkers) {
      series[1].setMarkers(markersDiv.concat(pendingMarkerDiv))
    }
  }

  if (newMarkerPivot || (pendingMarkerPivot && !newMarkerPivot)) {
      pendingMarkerPivot = newMarkerPivot
    
    if (markersPivot.length > 100) {
        markersPivot.splice(0, 1)
    }
      
    if (options.toggleShowPricePivot && series[2].setMarkers) {
      series[2].setMarkers(markersPivot.concat(pendingMarkerPivot))
    }
  }

  // </markers utils>
}

