  // find pivots low (cvd and price)
  plCvd = pivot_low(cvd, options.pivotLeftLength, options.pivotRightLength)
  plPrice = pivot_low($price.low, options.pivotLeftLength, options.pivotRightLength)

  // find pivots high (cvd and price)
  phCvd = pivot_high(cvd, options.pivotLeftLength, options.pivotRightLength)
  phPrice = pivot_high($price.high, options.pivotLeftLength, options.pivotRightLength)
  
  if( plPrice ){
    plPositions.unshift(bar.length)
    plVals.unshift(plPrice)
    if( plPositions.length > pivotsMax ){
      plPositions.pop();
      plVals.pop()
    }

    var foundDiv = false
    var startPoint = 0
    cvd[100] // looks like required to load 
    for( var i = 0; i < (options.ppCheck || 10); i++ ){ 
      if( i > plPositions.length || plPositions[i] == 0){
        break
      }
      var len = bar.length - plPositions[i] + options.pivotRightLength
      if( len > (options.maxbars || 100)){
        break
      }
      
      // Price lower than previous pivot but CVD is higher
      if( len > 5 && plPrice < (plVals[i] || 0) && cvd > cvd[len]){
         console.log('PL', plPositions[i], len, cvd, cvd[len], $price.high, plVals[i])
         foundDiv = true

         newMarkerDiv = { 
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.DivcolorUp,
          position: 'belowBar',
          //shape: 'arrowDown',
          text: '' + (options.togglePivotAgo ? i : '')
        }
        break
      }
    }

    if( !foundDiv && options.toggleShowPricePivot){
      newMarkerPivot = {
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.DivcolorUp,
          position: 'belowBar',
          //shape: 'arrowUp',
          text:  'L'
        }
    }
  }

  if( phPrice ){
 
    phPositions.unshift(bar.length)
    phVals.unshift(phPrice)
    if( phPositions.length > pivotsMax ){
      phPositions.pop();
      phVals.pop()
    }

    var foundDiv = false
    var startPoint = 0
    cvd[100] // looks like required to load 
    for( var i = 0; i < (options.ppCheck || 10); i++ ){ 
      if( i > phPositions.length || phPositions[i] == 0){
        break
      }
      var len = bar.length - phPositions[i] + options.pivotRightLength
      if( len > (options.maxbars || 100)){
        break
      }

      // Price greater than previous pivot but CVD lower
      if( len > 5 && phPrice > (phVals[i] || Infinity) && cvd < cvd[len]){
         console.log('PH', phPositions[i], len, cvd, cvd[len], $price.high, phVals[i])
        foundDiv = true
         newMarkerDiv = { 
          time: time - (options.pivotRightLength-1) * bar.timeframe,
          color: options.DivcolorDn,
          position: 'aboveBar',
          //shape: 'arrowDown',
          text: '' + (options.togglePivotAgo ? i : '')
        }
        break
      }
    }

    if( !foundDiv  && options.toggleShowPricePivot ){
        newMarkerPivot = { 
            time: time - (options.pivotRightLength-1) * bar.timeframe,
            color: options.DivcolorDn,
            position: 'aboveBar',
            //shape: 'arrowDown',
            text: 'H'
          }
    }
  }
  
  // <markers utils>
  if (newMarkerDiv || (pendingMarkerDiv && !newMarkerDiv)) {
      pendingMarkerDiv = newMarkerDiv
    
    if (markersDiv.length > 100) {
        markersDiv.splice(0, 1)
    }
      
    if (series[0].setMarkers) {
      series[0].setMarkers(markersDiv.concat(pendingMarkerDiv))
    }
  }

  if (newMarkerPivot || (pendingMarkerPivot && !newMarkerPivot)) {
      pendingMarkerPivot = newMarkerPivot
    
    if (markersPivot.length > 100) {
        markersPivot.splice(0, 1)
    }
      
    if (series[1].setMarkers) {
      series[1].setMarkers(markersPivot.concat(pendingMarkerPivot))
    }
  }

  // </markers utils>
}

