if (!pendingMarkers) {
    // runs only once
    pendingMarkers = []

    showType = {
      spot: options.toggleSpot,
      perp: options.togglePerp,
      binance: options.toggleBinance,
      coinbase: options.toggleCoinbase,
      bitmex: options.toggleBitmex,
      binancefuture: options.toggleBinanceFuture
    }

    toAdd = [
        options.toggleBand1 || false,
        options.toggleBand2 || false,
        options.toggleBand3 || false,
        options.toggleBand4 || false,
    ]
}
// </STARTUP SCRIPT> 
var bidsPerp = ORDERBOOK:AGGRPERP-BTCUSD.zbids
var asksPerp = ORDERBOOK:AGGRPERP-BTCUSD.zasks
var bidsSpot = ORDERBOOK:AGGRSPOT-BTCUSD.zbids 
var asksSpot = ORDERBOOK:AGGRSPOT-BTCUSD.zasks 
var bidsBinance = renderer.sources['ORDERBOOK:BINANCE-BTCUSDT'] && renderer.sources['ORDERBOOK:BINANCE-BTCUSDT'].zbids
var asksBinance = renderer.sources['ORDERBOOK:BINANCE-BTCUSDT'] && renderer.sources['ORDERBOOK:BINANCE-BTCUSDT'].zasks
var bidsCoinbase = renderer.sources['ORDERBOOK:COINBASE-BTCUSD'] && renderer.sources['ORDERBOOK:COINBASE-BTCUSD'].zbids
var asksCoinbase = renderer.sources['ORDERBOOK:COINBASE-BTCUSD'] && renderer.sources['ORDERBOOK:COINBASE-BTCUSD'].zasks
var bidsBitmex = renderer.sources['ORDERBOOK:BITMEX-XBTUSD'] && renderer.sources['ORDERBOOK:BITMEX-XBTUSD'].zbids
var asksBitmex = renderer.sources['ORDERBOOK:BITMEX-XBTUSD'] && renderer.sources['ORDERBOOK:BITMEX-XBTUSD'].zasks
var bidsBf = renderer.sources['ORDERBOOK:BINANCEFUTURES-BTCUSDT'] && renderer.sources['ORDERBOOK:BINANCEFUTURES-BTCUSDT'].zbids
var asksBf = renderer.sources['ORDERBOOK:BINANCEFUTURES-BTCUSDT'] && renderer.sources['ORDERBOOK:BINANCEFUTURES-BTCUSDT'].zasks

var m = [
    { which: 'perp', bids: bidsPerp || [], asks: asksPerp || [] },
    { which: 'spot', bids: bidsSpot || [], asks: asksSpot || [] },
    { which: 'binance', bids: bidsBinance || [], asks: asksBinance || [] },
    { which: 'coinbase', bids: bidsCoinbase || [], asks: asksCoinbase || [] },
    { which: 'bitmex', bids: bidsBitmex || [], asks: asksBitmex || [] },
     { which: 'binancefuture', bids: bidsBf || [], asks: asksBf || [] }
];

var _bids = 0
var _asks = 0

for(let typeIndex = 0; typeIndex < m.length; typeIndex++){

    var c = m[typeIndex]

    if( showType[c.which]  === false ){
      continue
    }

    for(let i = 0; i < 4; i++){
        if( toAdd[i] === true ){
            _bids += c.bids[i] || 0
            _asks += c.asks[i] || 0
        }
    }
}
// cloudarea(lowerValue: number, higherValue: number)
// - `higherValue` (`number`) - the value of the item which is expected to be higher (default: green line)
// - `lowerValue` (`number`) - the value of the item which is expected to be lower (default: red line)
plotcloudarea(_asks, _bids, positiveColor=options.positiveColor,  negativeColor=options.negativeColor,  higherLineColor=options.positiveLineColor, lowerLineColor=options.negativeLineColor)

